svg.script:
//    
    let levels = [[0, 0],[0, 5],[5,12],[12,21],[21,34],[34,53],[53, 999]];
let credits = 0;

    let run = function(){
console.log('running');
let credits = localStorage.getItem("credits");
console.log(credits);
if(document.querySelector(".Maxed")){
document.querySelector(".Maxed").classList.remove("Maxed");}
levels.forEach((level)=>{
        if(credits > level[0] && credits <= level[1]){
document.querySelector('.cls-1').setAttribute("width", (1000 * ((credits - level[0])/(level[1] - level[0]))));
}
} )
if(credits > 53){
document.querySelector('.cls-1').setAttribute("width", 1000);
document.querySelector('.cls-1').classList.add("Maxed");
}
}                                                

        document.addEventListener("DOMContentLoaded", run);
        window.addEventListener('storage', run);
 //
